import React, {useState, useEffect} from 'react';
import Bar from './Bar'
import styled from 'styled-components';
import {MergeSort} from './Sorting Algorithms';

const Container = styled.div `
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 20px;
    postion: absolute;
`;

const Button = styled.button`
    padding: 10px 20px;
    font-size: 16px;
    margin-bottom: 20px;
    cursor: pointer;
`;

const ArrayContainer = styled.div`
    display: flex;
    justify-content: center; 
    transform: scaleY(-1);
    bottom: 40px;
    position: absolute; 
`;

const SortingVisualiser = () => {
    const [array, setArray] = useState([]);
    const [animations, setAnimations] = useState([]);

    useEffect(() => {
        resetArray();
    }, []);

    const resetArray = () => {
        const newArray = [];
        for (let i = 0; i < 130; i++) {
            newArray.push(Math.floor(Math.random() *450) + 1);
        }
        setArray(newArray);
        setAnimations([]);
    };

    const handleMergeSort = () => {
        const animations = [];
        const sortedArray = MergeSort([...array], animations);
        animateSorting(animations, sortedArray);
    };

    const animateSorting = (animations, sortedArray) => {
        animations.forEach((animation, index) => {
            setTimeout(() => {
                setArray((prevArray) => {
                    const newArray = [...prevArray];
                    newArray[animation.index] = animation.value;
                    return newArray;
                });
            }, index * 50); // Adjust the delay for faster/slower animations
        });
    
        // After the animations are done, set the sorted array
        setTimeout(() => {
            setArray(sortedArray);
        }, animations.length*5);
    };

    return (
        <Container>
            <Button onClick = {resetArray}>Generate New Array</Button>
            <Button onClick = {handleMergeSort}>Merge Sort</Button>
            <ArrayContainer>
                {array.map((value, idx) => (
                    <Bar key = {idx} height = {value} />
                ))}
            </ArrayContainer>
        </Container>
    );
};

export default SortingVisualiser;
