import React, { useState, useRef } from 'react';
import Bar from './Bar';
import styled from 'styled-components';
import { mergeSort } from './Sorting Algorithms'; // Importing mergeSort function

const ArrayContainer = styled.div`
    display: flex;
    justify-content: center;
    transform: scaleY(-1);
    margin-top: 120px;
    position: relative;
`;

const Container = styled.div`
    display: inline-block;
    flex-direction: column;
    align-items: center;
    margin-top: 20px;
    position: relative;
    bottom: 40px;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    margin-top: 20px;
`;

const Button = styled.button`
    padding: 10px 20px;
    font-size: 16px;
    margin-right: 10px;
    cursor: pointer;
`;

const SortingVisualiser = () => {
    const [array, setArray] = useState([]);
    const timeouts = useRef([]);

    const generateRandomArray = (length, maxValue) => {
        return Array.from({ length }, () => Math.floor(Math.random() * maxValue) + 1);
    };

    const clearAllTimeouts = () => {
        timeouts.current.forEach(timeout => clearTimeout(timeout));
        timeouts.current = [];
    };

    const handleResetArray = () => {
        clearAllTimeouts();
        const newArray = generateRandomArray(130, 450); // Generate a new random array
        setArray(newArray);
    };

    const handleMergeSort = () => {
        clearAllTimeouts();
        const animations = mergeSort([...array]); // Get sorting animations
        animateSorting(animations);
    };

    const animateSorting = (animations) => {
        let tempArray = [...array];
        animations.forEach((animation, index) => {
            const { comparison, swap } = animation;
            const timeout = setTimeout(() => {
                if (swap) {
                    const [idx, newValue] = swap;
                    tempArray[idx] = newValue;
                    setArray([...tempArray]);
                }
            }, index*1); 
            timeouts.current.push(timeout);
        });
    };

    return (
        <Container>
            <ButtonContainer>
            <Button onClick={handleResetArray}>Reset Array</Button>
            <Button onClick={handleMergeSort}>Merge Sort</Button>
            </ButtonContainer>
            <ArrayContainer>
                {array.map((value, idx) => (
                    <Bar key={idx} height={value} />
                ))}
            </ArrayContainer>
        </Container>
    );
};
export default SortingVisualiser;
