import React from 'react';
import styled from 'styled-components';
const StyledBar = styled.div`
        position: relative;
        height: ${({ height }) => `${height}px`};
        width: 8px;
        background-color: teal;
        display: inline-block;
        margin: 0 1px;
        transition: height 0.3s ease;
`;

export default Bar;
