import React from 'react';
import styled from 'styled-components';
import SortingVisualiser from './components/Sorting Visualiser';

const AppContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 100vh;
`;

const Header = styled.h1`
    margin-top: 20px;
`;

const ButtonContainer = styled.div`
    margin-top: 20px;
`;

const Button = styled.button`
    padding: 10px 20px;
    font-size: 16px;
    margin-right: 10px;
    cursor: pointer;
`;

const App = () => {
    const handleResetArray = () => {
        resetArray();
    };

    const resetArray = () => {
      const newArray = Array.from({ length: 130 }, () => Math.floor(Math.random() * 450) + 1);
      setArray(newArray);
    };


    return (
        <AppContainer>
            <Header>Sorting Visualizer</Header>
            <ButtonContainer>
                <Button onClick={handleResetArray}>Generate New Array</Button>
                <Button onClick={handleMergeSort}>Merge Sort</Button>
            </ButtonContainer>
            <SortingVisualiser />
        </AppContainer>
    );
};

export default App;
