// App.js
import React from 'react';
import styled from 'styled-components';
import SortingVisualiser from './SortingVisualiser'; // Import SortingVisualiser component

const AppContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 100vh;
`;

const Header = styled.h1`
    margin-top: 20px;
`;

const ButtonContainer = styled.div`
    margin-top: 20px;
`;

const Button = styled.button`
    padding: 10px 20px;
    font-size: 16px;
    margin-right: 10px;
    cursor: pointer;
`;

const App = () => {
    return (
        <AppContainer>
            <Header>Sorting Visualizer</Header>
            <ButtonContainer>
                {/* Button for generating a new array */}
                <Button onClick={handleResetArray}>Generate New Array</Button>
                {/* Button for triggering merge sort */}
                <Button onClick={handleMergeSort}>Merge Sort</Button>
            </ButtonContainer>
            {/* Render the SortingVisualiser component */}
            <SortingVisualiser />
        </AppContainer>
    );
};

export default App;
