import React, { useState, useEffect } from 'react';
import Bar from './Bar';
import styled from 'styled-components';
import { mergeSort } from './SortingAlgorithms'; // Importing mergeSort function

const ArrayContainer = styled.div`
    display: flex;
    justify-content: center;
    transform: scaleY(-1);
    bottom: 40px;
    position: absolute;
`;

const SortingVisualiser = () => {
    const [array, setArray] = useState([]);

    useEffect(() => {
        resetArray();
    }, []);

    const resetArray = () => {
        const newArray = generateRandomArray(130, 450); // Generate a new random array
        setArray(newArray);
    };

    const generateRandomArray = (length, maxValue) => {
        return Array.from({ length }, () => Math.floor(Math.random() * maxValue) + 1);
    };

    const handleMergeSort = () => {
        const animations = mergeSort(array); // Get sorting animations
        animateSorting(animations); // Start animating the sorting process
    };

    const animateSorting = (animations) => {
        animations.forEach((animation, index) => {
            const { comparison, swap } = animation;
            setTimeout(() => {
                if (comparison) {
                    const [leftIndex, rightIndex] = comparison;
                    // Implement visual highlighting for comparison (if needed)
                }
                if (swap) {
                    setArray((prevArray) => {
                        const newArray = [...prevArray];
                        const [idx, newValue] = swap;
                        newArray[idx] = newValue;
                        return newArray;
                    });
                }
            }, index * 10); // Adjust the delay for faster/slower animations
        });
    };

    return (
        <div>
            <ArrayContainer>
                {array.map((value, idx) => (
                    <Bar key={idx} height={value} />
                ))}
            </ArrayContainer>
            {/* Button for resetting the array */}
            <button onClick={resetArray}>Reset Array</button>
            {/* Button for triggering merge sort */}
            <button onClick={handleMergeSort}>Merge Sort</button>
        </div>
    );
};

export default SortingVisualiser;
